INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Shootmon', 'Digimon', 'Black', 'Ultimate', 'Machine', 'Vaccine', 5, 6, 3, 'Common', NULL, 6000, '[On Play] [When Digivolving] If you have 3 or more security cards, ＜De-Digivolve 1＞ 1 of your opponent''s Digimon (Trash the top card. You can''t trash past level 3 cards). If you have 3 or fewer, delete 1 of their Digimon with a play cost of 6 or less.', '[End of Attack] [Once Per Turn] If this Digimon has [Pulsemon]&#160;in its text, by trashing the top card of your security stack, unsuspend this Digimon.', 'BT-16: Booster Beginning Observer', 'https://images.digimoncard.io/images/cards/BT16-059.jpg', 'BT16', '059', 'DG');
