INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('WereGarurumon', 'Digimon', 'Blue', 'Ultimate', 'Beastkin', 'Vaccine', 5, 4, 3, 'Alternative Art', NULL, 7000, '[Hand] [Counter] ＜Blast Digivolve＞ (Your Digimon may digivolve into this card without paying the cost). [On Play] [When Digivolving] ＜Draw 1＞ (Draw 1 card from your deck). Then, if you have 5 or more cards in your hand, trash 1 card in your hand. [All Turns] [Once Per Turn] When one of your Digimon''s effects adds cards to your hand, 1 of your opponent''s Digimon or Tamers can''t suspend until the end of their turn.', 'Ace Overflow ＜-3＞ (As this card moves from the battle area or under a card to another area, lose 3 memory.).', 'BT-15: Booster Exceed Apocalypse', 'https://images.digimoncard.io/images/cards/BT15-026.jpg', 'BT15', '026', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Divermon', 'Digimon', 'Blue', 'Ultimate', 'Aquabeast', 'Data', 5, 6, 3, 'Common', NULL, 7000, '[On Play] Trash the bottom 3 digivolution cards of 1 of your opponent''s Digimon. Then, if your opponent has no Digimon with digivolution cards, you may play 1 blue Tamer card from your hand without paying the cost.', NULL, 'BT-15: Booster Exceed Apocalypse', 'https://images.digimoncard.io/images/cards/BT15-028.jpg', 'BT15', '028', 'DG');
