INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Minervamon', 'Digimon', 'Purple', 'Mega', 'Shaman', 'Virus', 6, 12, 3, 'Alternative Art', NULL, 11000, '＜Retaliation＞ (When this Digimon is deleted after losing a battle, delete the Digimon it was battling). [Opponent''s Turn] When your opponent plays a Digimon, you may plays 1 purple level 4 or lower Digimon card from your trash without paying its memory cost. Any [On Play] effects on Digimon played with this effect don''t activate. [On Deletion] If your opponent has 2 or fewer Digimon in play, you may play 1 purple level 5 or lower Digimon card or 1 [Mervamon] from your trash without paying its memory cost.', NULL, 'BT-10: Booster Xros Encounter', 'https://images.digimoncard.io/images/cards/BT10-083.jpg', 'BT10', '083', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Phoenixmon', 'Digimon', 'Red', 'Mega', 'Holy Beast', 'Vaccine', 6, 11, 3, 'Alternative Art', 'Takase', 11000, '[Your Turn] [Once Per Turn] When a card is removed from your opponent''s security stack, you may activate 1 of this Digimon''s [On Deletion] effects. [On Deletion] You may play 1 red Digimon with &#91;Avian]/&#91;Bird]/&#91;Beast]/&#91;Animal]/&#91;Sovereign]&#160;in one of its traits (other than [Sea Animal]), and 3000 DP or less from your hand without paying the cost. For each red Tamer you have in play, add 2000 to the maximum DP of the card you can play by this effect.', NULL, 'BT-11: Booster Dimensional Phase', 'https://images.digimoncard.io/images/cards/BT11-016.jpg', 'BT11', '016', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('ZeigGreymon', 'Digimon', 'Blue', 'Champion', 'Cyborg', 'Virus', 6, 11, 3, 'Rare', 'TANIMESO', 11000, '[When Digivolving] Unsuspend this Digimon. Then, if a card with the [Blue Flare]/[Xros Heart]&#160;trait is in this Digimon''s digivolution cards and your opponent has 2 or more Digimon in play, gain 2 memory. [On Deletion] ＜Save＞ (You may place this card under one of your Tamers). Then place 1 blue [Greymon]&#160;in its name and 1 blue [MailBirdramon] from your trash under 1 of your Tamers with the [General]&#160;trait.', '[Opponent''s Turn] While this Digimon has the [Blue Flare]&#160;trait, it gains ＜Blocker＞ (At blocker timing, by suspending this Digimon, it becomes the attack target).', 'BT-11: Booster Dimensional Phase', 'https://images.digimoncard.io/images/cards/BT11-031.jpg', 'BT11', '031', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('MetalEtemon', 'Digimon', 'Yellow', 'Mega', 'Cyborg', 'Virus', 6, 11, 3, 'Uncommon', 'GOSSAN', 11000, '[On Play] [When Digivolving] By Reveal the top 4 cards of your deck. You may play any number of Digimon cards with [Chuumon]/[Sukamon]/[Etemon]&#160;in their names whose total play costs add up to 7 or less among them without paying the costs. Trash the rest.', NULL, 'BT-11: Booster Dimensional Phase', 'https://images.digimoncard.io/images/cards/BT11-044.jpg', 'BT11', '044', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Jijimon', 'Digimon', 'Green', 'Mega', 'Ancient', 'Vaccine', 6, 11, 3, 'Alternative Art', 'koki', 11000, '[When Digivolving] Reveal the top 3 cards of your deck. You may play 1 Tamer card among them without paying the cost. Place the rest at the top or bottom of your deck in any order. [When Attacking] [Once Per Turn] For each green or black Tamer you have in play, reveal 1 card from the top of your deck. You may play any number of green or black Digimon cards whose total play costs add up to 10 or less among them without paying the costs. Place the rest at the bottom of your deck in any order.', NULL, 'BT-11: Booster Dimensional Phase', 'https://images.digimoncard.io/images/cards/BT11-056.jpg', 'BT11', '056', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Machinedramon', 'Digimon', 'Black', 'Mega', 'Machine', 'Virus', 6, 11, 3, 'Rare', 'kaz', 11000, '[When Digivolving] [On Play] Reveal the top 5 cards of your deck. Add 1 [Analogman] among them to your hand, and add 1 card with the [Cyborg]/[Machine]&#160;trait to your hand or place it under this Digimon as its bottom digivolution card. Trash the rest. [On Deletion] By placing 1 of your in play [Analogman]s at the bottom of its owner''s deck, you may play 1 [Machinedramon] from your hand without paying its cost.', NULL, 'BT-11: Booster Dimensional Phase', 'https://images.digimoncard.io/images/cards/BT11-072.jpg', 'BT11', '072', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('EmperorGreymon', 'Digimon', 'Red', 'Champion', 'Dragon Warrior', 'Variable', 6, 11, 3, 'Alternative Art', NULL, 11000, '＜Security Attack +1＞ (This Digimon checks 1 additional security card) [When Digivolving] Delete 1 of your opponent''s Digimon with 6000 DP or less. If this Digimon has a red Tamer card in its digivolution cards, delete 1 of your opponent’s Digimon with DP less than or equal to this Digimon’s DP instead. [On Deletion] You may play 1 [Takuya Kanbara] from your hand or trash without paying the cost.', NULL, 'BT-12: Booster Across Time', 'https://images.digimoncard.io/images/cards/BT12-017.jpg', 'BT12', '017', 'DG');
