INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Machinedramon', 'Digimon', 'Black', 'Mega', 'Machine', 'Virus', 6, 11, 3, 'Rare', NULL, 11000, '[On Play] [When Attacking] ＜De-Digivolve 2＞ 1 of your opponent''s Digimon (Trash up to 2 cards from the top. You can''t trash past level 3 cards). [Your Turn] This Digimon can only digivolve into white Digimon. [End of Opponent''s Turn] Delete this Digimon. Then, you may play 1 Digimon card with the [Dark Masters]&#160;trait, other than [Machinedramon], from your hand without paying the cost.', '＜Reboot＞ (Unsuspend this Digimon during your opponent''s unsuspend phase).', 'BT-15: Booster Exceed Apocalypse', 'https://images.digimoncard.io/images/cards/BT15-066.jpg', 'BT15', '066', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Piedmon', 'Digimon', 'Purple', 'Mega', 'Wizard', 'Virus', 6, 11, 3, 'Rare', NULL, 11000, '[On Play] [When Attacking] Delete 1 of your opponent’s unsuspended Digimon. [Your Turn] This Digimon can only digivolve into white Digimon. [End of Opponent''s Turn] Delete this Digimon. Then, you may play 1 Digimon card with the [Dark Masters]&#160;trait, other than [Piedmon], from your hand without paying the cost.', '＜Retaliation＞ (When this Digimon is deleted after losing a battle, delete the Digimon it was battling).', 'BT-15: Booster Exceed Apocalypse', 'https://images.digimoncard.io/images/cards/BT15-079.jpg', 'BT15', '079', 'DG');
