INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Ravemon', 'Digimon', 'Purple', 'Mega', 'Cyborg', 'Vaccine', 6, 11, 3, 'Alternative Art', NULL, 11000, '＜Jamming＞ (This Digimon can''t be deleted in battles against Security Digimon). [End of Attack] By deleting this Digimon that has a digivolution card with &#91;Bird]&#160;in one of its traits, at the end of your opponent''s turn, play 1 [Ravemon] from your trash without paying the cost. [On Deletion] If your opponent has 8 or more cards in their hand, they trash 1 card in their hand. Then, if your opponent has 7 or fewer cards in their hand, they add the top card of their security stack to their hand.', NULL, 'EX-04: Theme Booster Alternative Being', 'https://images.digimoncard.io/images/cards/EX4-058.jpg', 'EX4', '058', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Mitamamon', 'Digimon', 'Yellow', 'Mega', 'God Beast', 'Vaccine', 6, 12, 3, 'Alternative Art', NULL, 11000, '[When Digivolving] [When Attacking] [Once Per Turn] By trashing the top card of your security stack, you may play 1 yellow level 4 or lower card from your hand without paying the cost. Digimon played by this effect gain ＜Rush＞ (This Digimon can attack the turn it comes into play) for the turn. [All Turns] All of your yellow Digimon gain ＜Barrier＞ (When this Digimon would be deleted in battle, by trashing the top card of your security stack, prevent that deletion). [Opponent''s Turn] All of your opponent''s Digimon with levels greater than or equal to the total cards in both players'' security stacks gain ＜Security Attack -2＞ (This Digimon checks 2 fewer security cards).', NULL, 'EX-05: Theme Booster Animal Colosseum', 'https://images.digimoncard.io/images/cards/EX5-033.jpg', 'EX5', '033', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Diaboromon', 'Digimon', 'Black', 'Mega', 'Unidentified', 'Unknown', 6, 11, 3, 'Alternative Art', NULL, 11000, '[Start of Your Main Phase] [When Digivolving] You may play 1 [Diaboromon] Token without paying the cost (Digimon/Cost 14/Lv.6/White/Mega/Unknown/Unidentified/3000 DP). [All Turns] [Once Per Turn] When an opponent''s Digimon is played, you may activate 1 of this Digimon''s [When Digivolving] effects. [All Turns] All of your other Digimon with [Diaboromon]&#160;in its name gain ＜Jamming＞ (This Digimon can''t be deleted in battles against Security Digimon) and ＜Blocker＞ (At blocker timing, by suspending this Digimon, it becomes the attack target).', NULL, 'EX-06: Theme Booster Infernal Ascension', 'https://images.digimoncard.io/images/cards/EX6-043.jpg', 'EX6', '043', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('DanDevimon', 'Digimon', 'Purple', 'Mega', 'Fallen Angel', 'Virus', 6, 11, 3, 'Rare', NULL, 11000, '[On Play] [When Digivolving] Delete 1 of your opponent''s level 5 or lower Digimon. If you didn''t, trash 1 card in your opponent''s hand. [Your Turn] While your opponent has 5 or fewer cards in their hand, this Digimon gains ＜Rush＞ (This Digimon can attack the turn it comes into play) and ＜Security Attack +1＞ (This Digimon checks 1 additional security card).', NULL, 'EX-06: Theme Booster Infernal Ascension', 'https://images.digimoncard.io/images/cards/EX6-055.jpg', 'EX6', '055', 'DG');
