INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('BeelStarmon', 'Digimon', 'Purple', 'Mega', 'Wizard', 'Virus', 6, 6, 3, 'Alternative Art', NULL, 11000, '[Hand] [Counter] ＜Blast Digivolve＞ (Your Digimon may digivolve into this card without paying the cost). [On Play] [When Digivolving] Return 1 Option card from your trash to the hand. Then, you may use 1 Option card with the [Three Musketeers]&#160;trait from your hand without paying the cost. [When Attacking] [Once Per Turn] By trashing 1 Option card from this Digimon''s digivolution cards, you may use 1 Option card with the [Three Musketeers]&#160;trait from your hand without paying the cost.', 'Ace Overflow ＜-4＞ (As this card moves from the battle area or under a card to another area, lose 4 memory.)', 'EX-07: Extra Booster Digimon Liberator', 'https://images.digimoncard.io/images/cards/EX7-059.jpg', 'EX7', '059', 'DG');
