INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Titamon', 'Digimon', 'Green', 'Mega', 'Shaman', 'Virus', 6, 12, 4, 'Uncommon', 'Nakano Haito', 12000, '＜Piercing＞ (When this Digimon attacks and deletes an opponent''s Digimon and survives the battle, it performs any security checks it normally would). [When Digivolving] You may trash up to 3 cards in your hand. If you do, for each card trashed, suspend 1 of your opponent''s Digimon. Then, for each suspended Digimon your opponent has in play, gain 1 memory.', NULL, 'BT-11: Booster Dimensional Phase', 'https://images.digimoncard.io/images/cards/BT11-057.jpg', 'BT11', '057', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('HerculesKabuterimon (X Antibody)', 'Digimon', 'Green', 'Mega', 'Insectoid', 'Vaccine', 6, 12, 4, 'Rare', 'Nakano Haito', 12000, '＜Security Attack +1＞ (This Digimon checks 1 additional security card). [When Digivolving] If this Digimon has [HerculesKabuterimon]/[X Antibody] in its digivolution cards, return 1 of your opponent''s suspended Digimon to the bottom of its owner''s deck.', NULL, 'BT-11: Booster Dimensional Phase', 'https://images.digimoncard.io/images/cards/BT11-058.jpg', 'BT11', '058', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Justimon: Accel Arm', 'Digimon', 'Black', 'Mega', 'Cyborg', 'Vaccine', 6, 12, 4, 'Uncommon', 'Tonamikanji', 12000, '[When Digivolving] By returning 1 level 6 Digimon card without [Justimon: Accel Arm] in its name from this Digimon''s digivolution cards to its owner''s hand, this Digimon gains ＜Security Attack +1＞ (This Digimon checks 1 additional security card) and ＜Piercing＞ (When this Digimon attacks and deletes an opponent''s Digimon and survives the battle, it performs any security checks it normally would) for the turn. [When Attacking] If you have a Tamer in play, this Digimon can digivolve into a Digimon card with [Justimon]&#160;in its name other than [Justimon: Accel Arm] in your hand for a digivolution cost of 2, ignoring its digivolution requirements.', NULL, 'BT-11: Booster Dimensional Phase', 'https://images.digimoncard.io/images/cards/BT11-073.jpg', 'BT11', '073', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Mervamon', 'Digimon', 'Purple', 'Mega', 'Shaman', 'Virus', 6, 11, 4, 'Alternative Art', 'Takase', 12000, 'When you would play this card, you can also place cards from your trash in digivolution cards for a DigiXros. [On Play] [When Digivolving] You may play 1 purple level 4 or lower Digimon card or 1 level 4 or lower Digimon card with the [Xros Heart]&#160;trait from your trash without paying the cost. If DigiXrosing, add 1 to the number of cards you play with this effect. [All Turns] All of your Digimon with the [Xros Heart]&#160;trait or with ＜Retaliation＞ gain ＜Rush＞ (This Digimon can attack the turn it comes into play) and ＜Blocker＞ (At blocker timing, by suspending this Digimon, it becomes the attack target).', NULL, 'BT-11: Booster Dimensional Phase', 'https://images.digimoncard.io/images/cards/BT11-086.jpg', 'BT11', '086', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Lilithmon', 'Digimon', 'Purple', 'Mega', 'Demon Lord', 'Virus', 6, 11, 4, 'Rare', 'Shin Sasaski', 12000, '[On Play] Trash the top 4 cards of your deck. Then, add up to 2 cards with &#91;Bagra Army]&#160;in one of their traits from your trash to your hand, and place up to 2 Digimon cards with the [Bagra Army]&#160;trait from your trash under 1 of your Tamers. [Opponent''s Turn] When your opponent moves a Digimon from their breeding area, by trashing 1 of this Digimon''s digivolution cards, that Digimon gains "[When Attacking] Lose 3 memory"" for the turn."', NULL, 'BT-11: Booster Dimensional Phase', 'https://images.digimoncard.io/images/cards/BT11-087.jpg', 'BT11', '087', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Gallantmon', 'Digimon', 'Red', 'Champion', 'Holy Warrior', 'Virus', 6, 12, 4, 'Alternative Art', NULL, 12000, '＜Raid＞ (When this Digimon attacks, you may switch the target of attack to 1 of your opponent''s unsuspended Digimon with the highest DP). [When Digivolving] Delete 1 of your opponent''s Digimon with 6000 DP or less. [When Attacking] Delete 1 of your opponent''s Digimon with 6000 DP or less. If no opponent''s Digimon was deleted by this effect, trash the top card of your opponent''s security stack.', NULL, 'BT-12: Booster Across Time', 'https://images.digimoncard.io/images/cards/BT12-018.jpg', 'BT12', '018', 'DG');
