INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Imperialdramon: Dragon Mode', 'Digimon', 'Blue', 'Mega', 'Ancient Dragon', 'Free', 6, 12, 4, 'Rare', NULL, 12000, '[When Digivolving] If this Digimon has a blue digivolution card, unsuspend this Digimon. If it has a green digivolution card, suspend 1 of your opponent''s Digimon. [End of Attack] You may digivolve this Digimon into a Digimon card with [Imperialdramon]&#160;in its name in your hand for its digivolution cost. When this Digimon would digivolve by this effect, reduce the digivolution cost by 2.', NULL, 'BT-12: Booster Across Time', 'https://images.digimoncard.io/images/cards/BT12-030.jpg', 'BT12', '030', 'DG');
