INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('ChaosGallantmon', 'Digimon', 'Red', 'Mega', 'Dark Knight', 'Virus', 6, 12, 4, 'Alternative Art', NULL, 12000, '[Trash] [End of Your Turn] By Deleting 1 of your Digimon with a digivolution card and [Gallantmon]&#160;in its name, you may play this card without paying its cost. [On Play] Delete 1 of your opponent''s Digimon with 7000 DP or less. For every 10 total cards in both player''s trashes, add 2000 to the maximum DP you can choose with DP-based deletion effects.', NULL, 'EX-04: Theme Booster Alternative Being', 'https://images.digimoncard.io/images/cards/EX4-011.jpg', 'EX4', '011', 'DG');
