INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('MedievalGallantmon', 'Digimon', 'Red', 'Mega', 'Warrior', 'Data', 6, 13, 4, 'Alternative Art', NULL, 12000, '[Security] Play this card without battling and without paying the cost. Return this Digimon to your hand at the end of the turn. [On Play] [When Attacking] Delete 1 of your opponent''s Digimon with 6000 DP or less. If no Digimon was deleted by this effect, suspend 1 of your opponent''s Digimon, and that Digimon doesn''t unsuspend during their next unsuspend phase.', NULL, 'EX-04: Theme Booster Alternative Being', 'https://images.digimoncard.io/images/cards/EX4-013.jpg', 'EX4', '013', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('GreyKnightsmon', 'Digimon', 'Blue', 'Mega', 'Dark Knight', 'Data', 6, 12, 5, 'Alternative Art', NULL, 12000, '[On Play] ＜De-Digivolve 1＞ 1 of your opponent''s Digimon (Trash the top card. You can''t trash past level 3 cards). Then, all of your opponent''s level 4 or lower Digimon can''t attack until the end of your opponent''s turn. [All Turns] When this Digimon would be deleted or returned to your hand or deck, you may play 1 [MetalGreymon] and 1 [DarkKnightmon] from this Digimon''s digivolution cards without paying the costs.', NULL, 'EX-04: Theme Booster Alternative Being', 'https://images.digimoncard.io/images/cards/EX4-021.jpg', 'EX4', '021', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('ZeedGarurumon', 'Digimon', 'Blue', 'Mega', 'Cyborg', 'Data', 6, 12, 4, 'Alternative Art', NULL, 12000, '[When Digivolving] Return 1 of your opponent''s level 4 or lower Digimon to its owner''s hand. Then, if your opponent has 8 or more cards in their hand, return 1 of your opponent''s level 6 or higher Digimon to its owner''s hand. [All Turns] [Once Per Turn] When an effect adds cards to your opponent''s hand, if you have a Tamer in play, return 1 of your opponent''s level 3 Digimon to its owner''s hand.', NULL, 'EX-04: Theme Booster Alternative Being', 'https://images.digimoncard.io/images/cards/EX4-022.jpg', 'EX4', '022', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Cherubimon', 'Digimon', 'Yellow', 'Mega', 'Cherub', 'Vaccine', 6, 12, 4, 'Rare', NULL, 12000, '＜Alliance＞ (When this Digimon attacks, by suspending 1 of your other Digimon, add the suspended Digimon''s DP to this Digimon and it gains ＜Security Attack +1＞ for the attack). [When Digivolving] [When Attacking] 1 of your opponent''s Digimon gets -3000 DP for the turn for each of your suspended Digimon.', NULL, 'EX-04: Theme Booster Alternative Being', 'https://images.digimoncard.io/images/cards/EX4-031.jpg', 'EX4', '031', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Gaiomon', 'Digimon', 'Black', 'Mega', 'Dragonkin', 'Virus', 6, 12, 4, 'Alternative Art', NULL, 12000, 'This card/Digimon is also treated as having [Greymon]&#160;in its name. [When Digivolving] Delete 1 of your opponent''s Digimon with a play cost of 13 or more. If no Digimon was deleted by this effect, trash the top card of your opponent''s security stack. [End of Your Turn] If you have a Tamer in play, this Digimon may digivolve into 1 card with [Gaiomon]&#160;in its name and a play cost of 13 or more in your hand, ignoring its digivolution requirements and without paying the cost.', NULL, 'EX-04: Theme Booster Alternative Being', 'https://images.digimoncard.io/images/cards/EX4-048.jpg', 'EX4', '048', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('CresGarurumon', 'Digimon', 'Black', 'Mega', 'Beast Knight', 'Data', 6, 12, 4, 'Alternative Art', NULL, 12000, '[When Digivolving] Activate 1 of the effects below. ・Choose any number of your opponent''s Digimon whose play costs add up to 6 or less and return them to the bottom of their owner''s deck. ・ Digivolve 1 of your other Digimon into a level 6 or lower Digimon card with [Greymon]&#160;in its name without paying its cost. ・This Digimon and another of your Digimon may DNA digivolve into a Digimon card in the hand.', '[When Attacking] [Once Per Turn] If this Digimon has [Omnimon]&#160;in its name, return 1 of your opponent''s level 5 or lower Digimon to the bottom of their deck.', 'EX-04: Theme Booster Alternative Being', 'https://images.digimoncard.io/images/cards/EX4-049.jpg', 'EX4', '049', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('ShadowSeraphimon', 'Digimon', 'Black', 'Champion', 'Seraph', 'Virus', 6, 12, 4, 'Alternative Art', NULL, 12000, '[Opponent''s Turn] When a card is removed from your security stack, ＜De-Digivolve 1＞ 1 of your opponent''s Digimon (Trash the top card. You can''t trash past level 3 cards). [On Deletion] ＜Recovery +1 (Deck)＞ (Place the top card of your deck on top of your security stack). Then, 1 of your opponent’s Digimon gets -4000 DP for the turn for each card in your security stack.', NULL, 'EX-04: Theme Booster Alternative Being', 'https://images.digimoncard.io/images/cards/EX4-050.jpg', 'EX4', '050', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('BlitzGreymon', 'Digimon', 'Black', 'Mega', 'Cyborg', 'Virus', 6, 12, 4, 'Alternative Art', NULL, 12000, '[When Digivolving] Activate 1 of the effects below. ・ ＜De-Digivolve 1＞ 3 of your opponent''s Digimon. ・ Digivolve 1 of your other Digimon into a level 6 or lower Digimon card with [Garurumon]&#160;in its name without paying its cost. ・This Digimon and another of your Digimon may DNA digivolve into a Digimon card in the hand.', '[When Attacking] [Once Per Turn] If this Digimon has [Omnimon]&#160;in its name, trash the top card of your opponent''s security stack.', 'EX-04: Theme Booster Alternative Being', 'https://images.digimoncard.io/images/cards/EX4-051.jpg', 'EX4', '051', 'DG');
