INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Ophanimon', 'Digimon', 'Yellow', 'Mega', 'Throne', 'Vaccine', 6, 7, 4, 'Alternative Art', NULL, 12000, '[Hand] [Counter] ＜Blast Digivolve＞ (Your Digimon may digivolve into this card without paying the cost). [On Play] [When Digivolving] By trashing the top or bottom card of your security stack, 1 of your opponent''s Digimon gets -8000 DP until the end of your opponents turn. [All Turns] [Once Per Turn] When a card is removed from your security stack, if it''s your turn, this Digimon may gain ＜Security Attack +1＞ (This Digimon checks 1 additional security card) for the turn and attack. If it''s your opponent''s turn, ＜Recovery +1 (Deck)＞ (Place the top card of your deck on top of your security stack).', 'Ace Overflow ＜-4＞ (As this card moves from the battle area or under a card to another area, lose 4 memory.)', 'EX-06: Theme Booster Infernal Ascension', 'https://images.digimoncard.io/images/cards/EX6-027.jpg', 'EX6', '027', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Seraphimon', 'Digimon', 'Yellow', 'Mega', 'Seraph', 'Vaccine', 6, 7, 4, 'Alternative Art', NULL, 12000, '[Hand] [Counter] ＜Blast Digivolve＞ (Your Digimon may digivolve into this card without paying the cost). [On Play] [When Digivolving] ＜Recovery +1 (Deck)＞ (Place the top card of your deck on top of your security stack). [All Turns] [Once Per Turn] When a card is added to your security stack, return 1 of your opponent''s Digimon with a level as high or lower than the number of cards in your security stack to the hand.', 'Ace Overflow ＜-4＞ (As this card moves from the battle area or under a card to another area, lose 4 memory.)', 'EX-06: Theme Booster Infernal Ascension', 'https://images.digimoncard.io/images/cards/EX6-028.jpg', 'EX6', '028', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Mastemon', 'Digimon', 'Yellow', 'Mega', 'Angel', 'Vaccine', 6, 7, 4, 'Alternative Art', NULL, 12000, '[Hand] [Counter] ＜Blast DNA Digivolve ([Angewomon] + [LadyDevimon])＞ (1 of your specified Digimon and 1 specified card in hand may DNA digivolve into this card.) [On Play] [When Digivolving] You may play 1 level 5 or lower Digimon card with the [Angel]/[Archangel]/[Fallen Angel]&#160;trait from your hand or trash without paying the cost. Then, if DNA digivolving, place 1 other Digimon at the bottom of its owner''s security stack, and trash cards from the top of your opponent''s security stack until it has 4 left.', 'Ace Overflow ＜-4＞ (As this card moves from the battle area or under a card to another area, lose 4 memory.)', 'EX-06: Theme Booster Infernal Ascension', 'https://images.digimoncard.io/images/cards/EX6-029.jpg', 'EX6', '029', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Dominimon', 'Digimon', 'Yellow', 'Mega', 'Dominion', 'Vaccine', 6, 12, 4, 'Alternative Art', NULL, 12000, '[When Digivolving] Search your security stack. You may play 1 level 5 or lower Digimon card with the [Angel]/[Archangel]&#160;trait among them without paying the cost. Then, shuffle your security stack, and 1 of your opponent''s Digimon gets -7000 DP until the end of the turn. [All Turns] When one of your Digimon with the [Angel]/[Archangel]/[Three Great Angels]&#160;trait would leave the battle area other than in battle, by trashing the top card of your security stack, prevent it from leaving. [Rule] Trait: Has the [Angel] type.', NULL, 'EX-06: Theme Booster Infernal Ascension', 'https://images.digimoncard.io/images/cards/EX6-030.jpg', 'EX6', '030', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Cherubimon', 'Digimon', 'Green', 'Mega', 'Cherub', 'Vaccine', 6, 7, 4, 'Alternative Art', NULL, 12000, '[Hand] [Counter] ＜Blast Digivolve＞ (Your Digimon may digivolve into this card without paying the cost). ＜Alliance＞ (When this Digimon attacks, by suspending 1 of your other Digimon, add the suspended Digimon''s DP to this Digimon and it gains ＜Security Attack +1＞ for the attack). [On Play] [When Digivolving] You may play 1 level 4 or lower green or yellow Digimon card from your hand without paying the cost. Then, 1 of your opponent''s Digimon gets -4000 DP for each of your other Digimon until the end of their turn.', 'Ace Overflow ＜-4＞ (As this card moves from the battle area or under a card to another area, lose 4 memory.)', 'EX-06: Theme Booster Infernal Ascension', 'https://images.digimoncard.io/images/cards/EX6-035.jpg', 'EX6', '035', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('BryweLudramon', 'Digimon', 'Black', 'Mega', 'Armor', 'Data', 6, 12, 4, 'Rare', NULL, 12000, '[Hand] [Main] By paying 3 cost and placing this card as the bottom digivolution card of 1 of your Digimon that''s level 6 or has the [Legend-Arms]&#160;trait, ＜De-Digivolve 1＞ all of your opponent''s Digimon with as much or less DP than that Digimon. ＜Blocker＞ (At blocker timing, by suspending this Digimon, it becomes the attack target). ＜Reboot＞ (Unsuspend this Digimon during your opponent''s unsuspend phase). [When Digivolving] 1 of your Digimon isn''t affected by the effects of your opponent''s Digimon, until the end of your opponent''s turn.', '＜Blocker＞ (At blocker timing, by suspending this Digimon, it becomes the attack target). [Opponent''s Turn] While this Digimon is [RagnaLoardmon], this Digimon can''t leave the battle area other than by your effects or by deletion.', 'EX-06: Theme Booster Infernal Ascension', 'https://images.digimoncard.io/images/cards/EX6-044.jpg', 'EX6', '044', 'DG');
