INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Goldramon (X Antibody)', 'Digimon', 'Red', 'Mega', 'Holy Dragon', 'Vaccine', 6, 13, 5, 'Rare', NULL, 13000, '＜Raid＞ (When this Digimon attacks, you may switch the target of attack to 1 of your opponent''s unsuspended Digimon with the highest DP). [When Digivolving] [When Attacking] You may use 1 [God Flame] or 1 Option card with the [Four Great Dragons]&#160;trait from your hand without paying the cost. [All Turns] This Digimon gains all effects of [Goldramon] in this Digimon''s digivolution cards.', NULL, 'BT-16: Booster Beginning Observer', 'https://images.digimoncard.io/images/cards/BT16-014.jpg', 'BT16', '014', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Phoenixmon (X Antibody)', 'Digimon', 'Red', 'Mega', 'Holy Beast', 'Vaccine', 6, 13, 5, 'Rare', NULL, 13000, '[When Digivolving] ＜Blitz＞ (If your opponent has 1 or more memory, this Digimon may attack). [Your Turn] While [Phoenixmon]/[X Antibody] is in this Digimon''s digivolution cards, attach [End of Attack] to all of this Digimon''s [On Deletion] effects. [On Deletion] You may play 1 11000 DP or lower red Digimon card with &#91;Avian]/&#91;Bird]/&#91;Beast]/&#91;Animal]/&#91;Sovereign]&#160;in one of its traits (other than [Sea Animal]) from your hand without paying the cost. Delete 1 of your opponent''s Digimon with as much or less DP as the Digimon this effect played.', NULL, 'BT-16: Booster Beginning Observer', 'https://images.digimoncard.io/images/cards/BT16-015.jpg', 'BT16', '015', 'DG');
