INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Darkdramon', 'Digimon', 'Black', 'Mega', 'Cyborg', 'Virus', 6, 13, 5, 'Rare', NULL, 13000, 'When this card would be played, if there is a Digimon with the [Boss]&#160;trait, reduce the play cost by 6. By returning 6 cards with the [D-Brigade]&#160;trait from your trash to the top of the deck, reduce the play cost by 6. [On Play] [When Digivolving] Reveal the top 3 cards of your deck. Delete 1 of your opponent''s Digimon with as high or lower a play cost as 1 Digimon card among them. Trash the revealed cards. [End of Your Turn] 2 of your Digimon may DNA digivolve into [Chaosmon] in the hand.', NULL, 'BT-16: Booster Beginning Observer', 'https://images.digimoncard.io/images/cards/BT16-065.jpg', 'BT16', '065', 'DG');
