INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Surfimon', 'Digimon', 'Blue', 'Mega', 'Cyborg', 'Vaccine', 6, 10, 3, 'Common', 'Kazumasa Yasukuni', 13000, NULL, NULL, 'BT-08: Booster New Awakening', 'https://images.digimoncard.io/images/cards/BT8-030.jpg', 'BT8', '030', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Imperialdramon Fighter Mode', 'Digimon', 'Blue', 'Ultimate', 'Ancient Dragonkin', 'Free', 6, 12, 5, 'Alternative Art', 'sasasi', 13000, '[When Digivolving] Return 1 of your opponent''s Digimon with 10000 DP or less to its owner''s hand. [When Attacking] [Once Per Turn] If a blue card is in this Digimon''s digivolution cards, unsuspend 1 of your Digimon. If a green card is in this Digimon''s digivolution cards, suspend 1 of your opponent''s Digimon.', NULL, 'BT-08: Booster New Awakening', 'https://images.digimoncard.io/images/cards/BT8-032.jpg', 'BT8', '032', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Spinomon', 'Digimon', 'Green', 'Mega', 'Dinosaur', 'Virus', 6, 10, 3, 'Common', 'Itohiro', 13000, NULL, NULL, 'BT-08: Booster New Awakening', 'https://images.digimoncard.io/images/cards/BT8-056.jpg', 'BT8', '056', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Rasenmon Fury Mode', 'Digimon', 'Purple', 'Mega', 'Mysterious Beast', 'Data', 6, 11, 3, 'Uncommon', 'Nakano Haito', 13000, '[End of Attack] You may digivolve this Digimon into a [Rasenmon] in your hand without paying its memory cost, ignoring its digivolution requirements. [End of Your Turn] Trash the top card of your security stack.', '[Your Turn] When this card is trashed as a digivolution card by the effect of [Rasenmon], unsuspend 1 of your Digimon and it gets +3000 DP for the turn.', 'BT-08: Booster New Awakening', 'https://images.digimoncard.io/images/cards/BT8-081.jpg', 'BT8', '081', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Dinorexmon', 'Digimon', 'Red', 'Mega', 'Dinosaur', 'Data', 6, 13, 5, 'Rare', 'KISUKE', 13000, '[When Digivolving] For each Tamer your opponent has in play, suspend 1 of your opponent’s Digimon and gain 1 memory. [All Turns] [Once Per Turn] When an opponent’s Digimon with 6000 DP or less becomes suspended, you may delete those Digimon.', NULL, 'BT-09: Booster X Record', 'https://images.digimoncard.io/images/cards/BT9-018.jpg', 'BT9', '018', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Gaiomon', 'Digimon', 'Black', 'Mega', 'Dragonkin', 'Virus', 6, 13, 5, 'Alternative Art', 'tyuga', 13000, 'This card/Digimon is also treated as having [Greymon]&#160;in its name. ＜Security Attack +1＞ (This Digimon checks 1 additional security card). ＜Reboot＞ (Unsuspend this Digimon during your opponent''s unsuspend phase). [When Digivolving] If this Digimon has a black digivolution card, ＜De-Digivolve 1＞ 1 of your opponent''s Digimon (Trash the top card. You can''t trash past level 3 cards). If this Digimon has a red digivolution card, ＜Blitz＞ (If your opponent has 1 or more memory, this Digimon may attack).', NULL, 'BT-09: Booster X Record', 'https://images.digimoncard.io/images/cards/BT9-068.jpg', 'BT9', '068', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Baihumon', 'Digimon', 'Black', 'Mega', 'Holy Beast', 'Data', 6, 13, 5, 'Super Rare', 'Naochika Morishita', 13000, '[When Digivolving] Unsuspend up to 2 Digimon and/or Tamers. Then, gain 1 memory for each of your opponent''s unsuspended Digimon and Tamers. [End of Your Turn] [Once Per Turn] For every 2 unsuspended Digimon and/or Tamers your opponent has in play, trash 1 card from the top card of your opponent''s security stack.', NULL, 'BT-09: Booster X Record', 'https://images.digimoncard.io/images/cards/BT9-069.jpg', 'BT9', '069', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('DexDorugoramon', 'Digimon', 'Purple', 'Mega', 'Undead', 'Virus', 6, 13, 5, 'Alternative Art', NULL, 13000, '[When Digivolving] If this Digimon has [Dorugoramon] in its digivolution cards, or is digivolved from the trash, delete all of your opponent''s Digimon with the lowest level. [On Deletion] You may play 1 purple or black level 3 Digimon card from your trash without paying its memory cost. If your have 5 or more cards with [Dex]/[DeathX]&#160;in their names in your trash, you may play 1 [DeathXmon] from your trash without paying its memory cost instead.', NULL, 'BT-09: Booster X Record', 'https://images.digimoncard.io/images/cards/BT9-081.jpg', 'BT9', '081', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Megidramon', 'Digimon', 'Red', 'Mega', 'Evil Dragon', 'Virus', 6, 13, 5, 'Alternative Art', 'Tonamikanji', 13000, 'The name of this card/Digimon is also treated as [ChaosGallantmon]. [When Digivolving] Delete 1 of your opponent''s Digimon with 10000 DP or less. If no Digimon was deleted by this effect, trash the top 5 cards of both players'' decks. [On Deletion] You may play 1 [Guilmon] and 1 [Takato Matsuki] from your hand and/or trash without paying their memory costs.', NULL, 'EX-02: Theme Booster Digital Hazard', 'https://images.digimoncard.io/images/cards/EX2-012.jpg', 'EX2', '012', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('MegaGargomon', 'Digimon', 'Green', 'Mega', 'Machine', 'Vaccine', 6, 12, 5, 'Alternative Art', 'GOSSAN', 13000, '[When Digivolving] For each green Tamer you have in play, suspend 1 of your opponent''s Digimon. They don''t unsuspend during your opponent''s next unsuspend phase. [When Attacking] [Once Per Turn] Return 1 of your opponent''s suspended Digimon with DP less than or equal to this Digimon''s DP to its owner''s hand.', NULL, 'EX-02: Theme Booster Digital Hazard', 'https://images.digimoncard.io/images/cards/EX2-029.jpg', 'EX2', '029', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Hydramon', 'Digimon', 'Green', 'Mega', 'Vegetation', 'Virus', 6, 13, 5, 'Alternative Art', 'Nakano Haito', 13000, '[When Digivolving] You may suspend 1 Digimon. [All Turns] [Once Per Turn] When an opponent''s Digimon becomes suspended, for each other suspended Digimon with &#91;Vegetation]/&#91;Plant]/&#91;Fairy]&#160;in one of its traits you have in play, gain 1 memory. [End of Your Turn] [Once Per Turn] If you have 2 or more suspended Digimon with &#91;Vegetation]/&#91;Plant]/&#91;Fairy]&#160;in one of their traits, return 1 of your opponent''s suspended Digimon at the bottom of its owner''s deck.', NULL, 'Draconic Roar EX-03', 'https://images.digimoncard.io/images/cards/EX3-045.jpg', 'EX3', '045', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Imperialdramon: Fighter Mode', 'Digimon', 'Purple', 'Mega', 'Ancient Dragonkin', 'Virus', 6, 13, 5, 'Alternative Art', 'Tonamikanji', 13000, '＜Piercing＞ (When this Digimon attacks and deletes an opponent''s Digimon and survives the battle, it performs any security checks it normally would). [When Digivolving] By returning 1 [Imperialdramon: Dragon Mode] from this Digimon''s digivolution cards to the bottom of its owner''s deck, none of your opponent''s [Security] effects can activate for the turn. [On Deletion] You may play 1 [Wormmon] and 1 [Veemon] from your trash without paying the costs.', NULL, 'EX-03: Theme Booster Draconic Roar', 'https://images.digimoncard.io/images/cards/EX3-073.jpg', 'EX3', '073', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('BlackMegaGargomon', 'Digimon', 'Green', 'Mega', 'Machine', 'Vaccine', 6, 13, 5, 'Alternative Art', NULL, 13000, '[End of Your Turn] [Once Per Turn] Until the end of your opponent''s turn, 2 of your green and/or black Digimon gain ＜Blocker＞ (At blocker timing, by suspending this Digimon, it becomes the attack target) and ＜Reboot＞ (Unsuspend this Digimon during your opponent''s unsuspend phase). [All Turns] [Once Per Turn] When another Digimon becomes suspended, you may unsuspend this Digimon.', NULL, 'EX-04: Theme Booster Alternative Being', 'https://images.digimoncard.io/images/cards/EX4-037.jpg', 'EX4', '037', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('HeavyLeomon', 'Digimon', 'Green', 'Mega', 'Machine', 'Vaccine', 6, 13, 5, 'Alternative Art', NULL, 13000, '＜Fortitude＞ (When this Digimon with digivolution cards is deleted, play this card without paying the cost). [When Digivolving] [On Deletion] ＜De-Digivolve 1＞ 1 of your opponent''s Digimon (Trash the top card. You can''t trash past level 3 cards). Then, return 1 of their 6000 DP or lower Digimon to the bottom of the deck. [End of Attack] [Once Per Turn] Return 1 of your opponent''s 4000 DP or lower Digimon to the bottom of the deck. If you didn''t, unsuspend this Digimon.', NULL, 'EX-05: Theme Booster Animal Colosseum', 'https://images.digimoncard.io/images/cards/EX5-055.jpg', 'EX5', '055', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Leviamon', 'Digimon', 'Purple', 'Mega', 'Demon Lord', 'Virus', 6, 13, 5, 'Alternative Art', NULL, 13000, '＜Security Attack +1＞ (This Digimon checks 1 additional security card) [On Play] [When Digivolving] If your opponent has as many or more total Digimon and Tamers as you, delete 1 of your opponent''s highest level Digimon. Then, delete 1 of your opponent''s lowest level Digimon. [All Turns] When an opponent''s Digimon is deleted, gain 1 memory for each Digimon deleted.', NULL, 'EX-05: Theme Booster Animal Colosseum', 'https://images.digimoncard.io/images/cards/EX5-063.jpg', 'EX5', '063', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Lucemon: Chaos Mode', 'Digimon', 'Purple', 'Ultimate', 'Demon Lord', 'Virus', 5, 13, 8, 'Alternative Art', NULL, 13000, '[On Play] [When Digivolving] Your opponent may delete 1 of their Digimon or Tamers. If this effect didn''t delete, trash the top card of your opponent''s security stack and ＜Recovery +1 (Deck)＞ (Place the top card of your deck on top of your security stack). [All Turns] When this Digimon would leave the battle area, by returning 1 [Lucemon]from this Digimon''s digivolution cards or from your trash to the bottom of the deck, you may play 1 [Lucemon: Satan Mode] or level 6 Digimon card with the [Seven Great Demon Lords]&#160;trait from your trash without paying the cost.', NULL, 'EX-06: Theme Booster Infernal Ascension', 'https://images.digimoncard.io/images/cards/EX6-054.jpg', 'EX6', '054', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Belphemon: Rage Mode', 'Digimon', 'Purple', 'Mega', 'Demon Lord', 'Virus', 6, 13, 5, 'Alternative Art', NULL, 13000, '[On Play] [When Digivolving] You may trash up to 3 cards in your hand. For each one, suspend 1 of your opponent''s level 5 or lower Digimon. Then, delete all of their lowest play cost suspended Digimon. [All Turns] When this Digimon would leave the battle area other than by battle, place 1 card with the [Seven Great Demon Lords]&#160;trait card from your trash as the bottom digivolution card of your [Gate of Deadly Sins] in the breeding area.', NULL, 'EX-06: Theme Booster Infernal Ascension', 'https://images.digimoncard.io/images/cards/EX6-060.jpg', 'EX6', '060', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Leviamon', 'Digimon', 'Purple', 'Mega', 'Demon Lord', 'Virus', 6, 13, 5, 'Alternative Art', NULL, 13000, '[All Turns] [Once Per Turn] When an opponent''s Digimon or one of your Digimon with the [Seven Great Demon Lords]&#160;trait is played, by trashing 1 card in your hand, return the bottom 3 digivolution cards of 1 of your opponent''s Digimon to the bottom of the deck. Then, if your opponent has as many or fewer total Digimon and Tamers as you, delete 1 of your opponent''s Digimon with no digivolution cards. [All Turns] When this Digimon would leave the battle area other than in battle, place 1 card with the [Seven Great Demon Lords]&#160;trait from your trash as the bottom digivolution card of one of your [Gate of Deadly Sins] in your breeding area.', NULL, 'EX-06: Theme Booster Infernal Ascension', 'https://images.digimoncard.io/images/cards/EX6-061.jpg', 'EX6', '061', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Volcanicdramon', 'Digimon', 'Red', 'Mega', 'Earth Dragon', 'Virus', 6, 3, 5, 'Alternative Art', NULL, 13000, '[On Play] [When Attacking] Delete 1 of your opponent''s Digimon with the lowest DP. [When Digivolving] Your opponent can''t play or move Digimon with 6000 DP or less until the end of their turn. [All Turns] [Once Per Turn] When this Digimon would leave battle area other than by one of your effects, you may play 1 Digimon card with the [Machine Dragon]/[Sky Dragon]&#160;trait from your hand without paying the cost.', NULL, 'EX-07: Extra Booster Digimon Liberator', 'https://images.digimoncard.io/images/cards/EX7-014.jpg', 'EX7', '014', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Metallicdramon', 'Digimon', 'Black', 'Mega', 'Sky Dragon', 'Data', 6, 3, 5, 'Alternative Art', NULL, 13000, '[On Play] [When Attacking] ＜De-Digivolve 4＞ 1 of your opponent''s Digimon (Trash up to 4 cards from the top. You can''t trash past level 3 cards). [When Digivolving] None of your opponent''s level 4 or lower Digimon can digivolve until the end of their turn. [All Turns] [Once Per Turn] When this Digimon would leave battle area other than by one of your effects, you may play 1 Digimon card with the [Rock Dragon]/[Earth Dragon]&#160;trait from your trash without paying the cost.', NULL, 'EX-07: Extra Booster Digimon Liberator', 'https://images.digimoncard.io/images/cards/EX7-049.jpg', 'EX7', '049', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Quantumon', 'Digimon', 'Yellow', 'Mega', 'Fairy', 'Data', 6, 13, 5, 'Secret Rare', NULL, 13000, '[When Digivolving] By placing 1 Digimon on top of its owner''s security stack, reveal all of your opponent''s security cards, and place 1 card among them on top of your opponent''s deck. Shuffle the rest and return them to the security stack. [Start of Opponent''s Turn] Declare 1 card category. Then, reveal the top card of your opponent''s deck. If that card is of the declared category, this Digimon isn''t affected by the effects of that card category for the turn. Return the revealed card to the top or the bottom of your opponent''s deck.', NULL, 'LM-01: Limited Pack Digimon Ghost Game', 'https://images.digimoncard.io/images/cards/LM-020.jpg', 'LM', '020', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('ShinMonzaemon', 'Digimon', 'Yellow', 'Mega', 'Puppet', 'Vaccine', 6, 13, 5, 'Alternative Art', NULL, 13000, '[When Digivolving] Place all level 3 Digimon face down on top of their owners'' security stacks in any order. Then, all of your opponent’s level 4 or higher Digimon get -3000 DP and ＜Security Attack -1＞ (This Digimon checks 1 fewer security card) until the end of their turn. [When Attacking] By trashing 1 card with [Numemon]&#160;in its name in this Digimon''s digivolution cards, place 1 of your opponent’s Digimon face down at the bottom of their security stack.', NULL, 'RB-01: Reboot Booster Rising Wind', 'https://images.digimoncard.io/images/cards/RB1-019.jpg', 'RB1', '019', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Arcturusmon', 'Digimon', 'Purple', 'Mega', 'Dragonkin', 'Virus', 6, 13, 5, 'Alternative Art', NULL, 13000, '[When Digivolving] You may place 1 Digimon card with [Gammamon]&#160;in its text from your trash as this Digimon''s bottom digivolution card. Then, you may delete 1 Digimon whose level is less than or equal to the number of cards in this Digimon''s digivolution cards. [On Deletion] You may return 1 Digimon Card from your trash to your hand. Then, by trashing 1 [Siriusmon] from your hand, you may play 1 [Proximamon] from your hand without paying its cost.', '[Your Turn] [Once Per Turn] When an opponent''s Digimon is deleted, if this Digimon has [Gammamon]&#160;in its text, trash the top card of your opponent’s security stack.', 'RB-01: Reboot Booster Rising Wind', 'https://images.digimoncard.io/images/cards/RB1-031.jpg', 'RB1', '031', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Mastemon', 'Digimon', 'Yellow', 'Mega', 'Angel', 'Vaccine', 6, 13, 5, 'Alternative Art', 'Tonamikanji', 13000, '[When Digivolving] Place 1 yellow or purple Digimon card from your trash on top of your security stack face down. When DNA digivolving, you may search your security stack for 1 level 5 or lower Digimon card and play it without paying its memory cost. Then, shuffle your security stack. [All Turns] When you play another Digimon using an effect, delete 1 of your opponent''s Digimon whose level is less than or equal to the played Digimon''s level.', NULL, 'AB-01: Digimon Card Game Adventure Box', 'https://images.digimoncard.io/images/cards/ST10-06.jpg', 'ST10', '06', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Galacticmon', 'Digimon', 'Black', 'Mega', 'Unknown', 'Unknown', 6, 14, 6, 'Alternative Art', 'Nakano Haito', 14000, '[When Digivolving] You may place up to 4 [Vemmon] from your trash under this Digimon as its bottom digivolution cards. Then, if there are 8 or more [Vemmon] in this Digimon''s digivolution cards, delete 1 of your opponent''s Digimon. [All Turns] When this Digimon would leave the battle area, by placing 4 [Vemmon] from this Digimon''s digivolution cards at the bottom of their owners'' decks, prevent it from leaving play. [Start of Your Main Phase] Trash the top card of your opponent''s security stack.', NULL, 'BT-11: Booster Dimensional Phase', 'https://images.digimoncard.io/images/cards/BT11-111.jpg', 'BT11', '111', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Examon', 'Digimon', 'Green', 'Mega', 'Holy Warrior', 'Data', 7, 14, 4, 'Alternative Art', NULL, 14000, '[On Play] [When Digivolving] Suspend 1 of your opponent''s Digimon. That Digimon doesn''t unsuspend during your opponent''s next unsuspend phase. [All Turns] [Once Per Turn] When an opponent''s Digimon becomes suspended, you may activate 1 of the effects below. ・ Suspend 1 of your opponent''s Digimon. ・ Unsuspend 1 of your Digimon.', NULL, 'BT-13: Booster Versus Royal Knights', 'https://images.digimoncard.io/images/cards/BT13-059.jpg', 'BT13', '059', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Belphemon: Rage Mode', 'Digimon', 'Purple', 'Mega', 'Demon Lord', 'Virus', 6, 14, 6, 'Alternative Art', NULL, 14000, '[Start of Your Main Phase] Delete all of your opponent''s level 5 or lower Digimon. Then if you have 6 or less cards in hand, this Digimon gets +3000 DP and ＜Security Attack +1＞ (This Digimon checks 1 additional security card) for the turn. [End of Attack] [Once Per Turn] By deleting 1 of your other Digimon, unsuspend this Digimon.', '[End of Opponent''s Turn] If this Digimon is [Belphemon: Sleep Mode], trash the top card of this Digimon.', 'BT-13: Booster Versus Royal Knights', 'https://images.digimoncard.io/images/cards/BT13-091.jpg', 'BT13', '091', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Omnimon', 'Digimon', 'White', 'Mega', 'Holy Warrior', 'Vaccine', 7, 14, 4, 'Alternative Art', NULL, 14000, '[On Play] [When Digivolving] Delete 1 of your opponent''s Digimon, or you may play 1 of each Digimon with the [Royal Knight]&#160;trait and a different name from the digivolution cards of a Digimon in your breeding area without paying their costs. When a Digimon is played by this effect, trash 1 of your Digimon in the breeding area, and all of your Digimon gain ＜Rush＞ (This Digimon can attack the turn it comes into play) for the turn.', NULL, 'BT-13: Booster Versus Royal Knights', 'https://images.digimoncard.io/images/cards/BT13-112.jpg', 'BT13', '112', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Leviamon (X Antibody)', 'Digimon', 'Purple', 'Mega', 'Demon Lord', 'Virus', 6, 14, 6, 'Alternative Art', NULL, 14000, '[Trash] [All Turns] When an effect plays an opponent''s Digimon or Tamer, 1 of your [Leviamon] or Digimon with [X Antibody] in their digivolution cards may digivolve into this card without paying the cost. ＜Security Attack +2＞ (This Digimon checks 2 additional security cards) [When Digivolving] If your opponent has as many or more total Digimon and Tamers as you, delete 1 of their Tamers. Then, delete 1 of your opponent''s level 3, 1 of their level 5 and 1 of their level 7 Digimon.', NULL, 'BT-15: Booster Exceed Apocalypse', 'https://images.digimoncard.io/images/cards/BT15-081.jpg', 'BT15', '081', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('TyrantKabuterimon', 'Digimon', 'Green', 'Mega', 'Insectoid', 'Virus', 6, 14, 6, 'Alternative Art', NULL, 14000, '[When Digivolving] You may play 1 Digimon card with the [Insectoid]/[Larva]&#160;trait from your hand with the play cost reduced by 8. [All Turns] While this Digimon is suspended, it isn''t affected by the effects of your opponent''s Digimon. [End of Your Turn] [Once Per Turn] By suspending 1 of your other Digimon, return 1 of your opponent''s Digimon with as much or less DP as the Digimon this effect suspended to the bottom of the deck.', NULL, 'BT-16: Booster Beginning Observer', 'https://images.digimoncard.io/images/cards/BT16-048.jpg', 'BT16', '048', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('RagnaLoardmon', 'Digimon', 'Red', 'Mega', 'Unique', 'Virus', 7, 14, 3, 'Alternative Art', 'sasasi', 14000, '＜Security Attack +1＞ (This Digimon checks 1 additional security card). ＜Reboot＞ (Unsuspend this Digimon during your opponent''s unsuspend phase). [When Digivolving] You may place 1 [Durandamon]/[BryweLudramon] from your hand on top of this card''s digivolution cards to gain 3 memory.', NULL, 'BT-03: Booster Union Impact', 'https://images.digimoncard.io/images/cards/BT3-019.jpg', 'BT3', '019', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Chaosmon', 'Digimon', 'White', 'Champion', 'Unique', 'Vaccine', 7, 15, 6, 'Alternative Art', 'funbolt', 14000, '＜Piercing＞ (When this Digimon attacks and deletes an opponent''s Digimon and survives the battle, it performs any security checks it normally would). [When Digivolving] Unsuspend this Digimon. Then, it can attack your opponent''s Digimon. This effect allows you to attack unsuspended Digimon as well.', NULL, 'BT-04: Booster Great Legend', 'https://images.digimoncard.io/images/cards/BT4-090.jpg', 'BT4', '090', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Chaosmon: Valdur Arm', 'Digimon', 'White', 'Mega', 'Unique', 'Vaccine', 7, 15, 6, 'Alternative Art', 'As''Maria', 14000, '[When Digivolving] Activate the following effect twice. ・1 of your opponent''s Digimon gets -7000 DP for the turn. [On Deletion] Gain 3 memory.', NULL, '1-Year Anniversary Promo Pack', 'https://images.digimoncard.io/images/cards/BT4-091.jpg', 'BT4', '091', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Omnimon', 'Digimon', 'White', 'Champion', 'Holy Warrior', 'Vaccine', 7, 15, 4, 'Alternative Art', '''''To be confirmed.''''', 14000, '[When Digivolving] ＜Blitz＞ (If your opponent has 1 or more memory, this Digimon may attack). [When Digivolving] Unsuspend this Digimon. [All Turns] When an opponent''s effect would delete this Digimon or return it to the hand or deck, by trashing 1 level 6 Digimon card in this Digimon''s digivolution cards, prevent it from leaving play.', NULL, 'BT-05: Booster Battle Of Omni', 'https://images.digimoncard.io/images/cards/BT5-086.jpg', 'BT5', '086', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Agumon - Bond of Bravery', 'Digimon', 'Red', 'Champion', 'Unknown', 'Unknown', 7, 14, 3, 'Alternative Art', 'Tonamikanji', 14000, '[When Attacking] If you have a Tamer in play, delete 1 of your opponent''s Digimon with 13000 DP or less. [Your Turn] [Once Per Turn] When an opponent''s Digimon is deleted, trash the top card of their security stack.', NULL, 'BT-06: Booster Double Diamond', 'https://images.digimoncard.io/images/cards/BT6-018.jpg', 'BT6', '018', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Gabumon - Bond of Friendship', 'Digimon', 'Blue', 'Mega', 'Unknown', 'Unknown', 7, 14, 3, 'Alternative Art', 'Tonamikanji', 14000, '[When Attacking] [Once Per Turn] If you have a Tamer in play, you may unsuspend this Digimon. [When Attacking] Return 1 of your opponent''s level 5 or lower Digimon to the bottom of its owner''s deck.', NULL, 'BT-06: Booster Double Diamond', 'https://images.digimoncard.io/images/cards/BT6-030.jpg', 'BT6', '030', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Aegisdramon', 'Digimon', 'Blue', 'Mega', 'Cyborg', 'Vaccine', 7, 14, 4, 'Alternative Art', 'Tonamikanji', 14000, '[When Digivolving] You may play 1 blue level 3 Digimon card or 1 Digimon card with [Seadramon]&#160;in its name or &#91;Aqua]/&#91;Sea Animal]&#160;in one of its traits from 1 of your blue Digimon''s digivolution cards without paying the cost. [Opponent''s Turn] [Once Per Turn] When your opponent plays a Digimon card, you may activate 1 of this Digimon''s [When Digivolving] effects.', NULL, 'EX-03: Theme Booster Draconic Roar', 'https://images.digimoncard.io/images/cards/EX3-026.jpg', 'EX3', '026', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Agumon - Bond of Bravery', 'Digimon', 'Red', 'Mega', 'Unknown', 'Unknown', 7, 8, 3, 'Promo', NULL, 14000, '[Hand] [Counter] ＜Blast Digivolve＞ (Your Digimon may digivolve into this card without paying the cost). [On Play] [When Digivolving] Delete any number of your opponent’s Digimon whose total DP adds up to equal or less than this Digimon’s DP. [When Attacking] [Once Per Turn] If you have a Tamer, trash the top card of your opponent’s security stack.', 'Ace Overflow ＜-5＞ (As this card moves from the battle area or under a card to another area, lose 5 memory.)', 'LM-03: Limited Card Set 2024', 'https://images.digimoncard.io/images/cards/LM-021.jpg', 'LM', '021', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Gabumon - Bond of Friendship', 'Digimon', 'Blue', 'Mega', 'Unknown', 'Unknown', 7, 8, 3, 'Promo', NULL, 14000, '[Hand] [Counter] ＜Blast Digivolve＞ (Your Digimon may digivolve into this card without paying the cost). [On Play] [When Digivolving] Return 1 of your opponent’s Digimon with equal or fewer digivolution cards than this Digimon to the bottom of the deck. [When Attacking] [Once Per Turn] If you have a Tamer, unsuspend this Digimon.', 'Ace Overflow ＜-5＞ (As this card moves from the battle area or under a card to another area, lose 5 memory.)', 'LM-03: Limited Card Set 2024', 'https://images.digimoncard.io/images/cards/LM-022.jpg', 'LM', '022', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Beelzemon: Blast Mode', 'Digimon', 'Purple', 'Mega', 'Demon Lord', 'Virus', 7, 14, 4, 'Super Rare', 'sasasi', 14000, 'When this card is trashed from your deck, delete 1 of your opponent''s level 3 or lower Digimon. For every 10 cards in your trash, add 1 to the maximum level of the Digimon you can choose with this effect. [When Digivolving] Unsuspend this Digimon. If you have 20 or more cards in your trash, gain 3 memory.', NULL, 'ST-14: Advanced Deck Beelzemon', 'https://images.digimoncard.io/images/cards/ST14-10.jpg', 'ST14', '10', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Omnimon', 'Digimon', 'White', 'Ultimate', 'Holy Warrior', 'Vaccine', 7, 15, 6, 'Alternative Art', 'sasasi', 15000, '[When Digivolving] Choose 1 of your opponent''s Digimon. Delete all of your opponent''s Digimon that share a name with it. [When Attacking] You can unsuspend this Digimon by returning 1 of this Digimon''s level 6 digivolution cards to your hand.', NULL, 'BT-01: Booster New Evolution', 'https://images.digimoncard.io/images/cards/BT1-084.jpg', 'BT1', '084', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Jesmon GX', 'Digimon', 'Red', 'Champion', 'Holy Warrior', 'Data', 7, 15, 5, 'Alternative Art', NULL, 15000, '[When Digivolving] You may place 1 card with the [Royal Knight]&#160;trait and a play cost of 13 or less from your hand or trash under this Digimon as its bottom digivolution card. Activate 1 of that Digimon''s [When Digivolving] effects as if it was one of this Digimon''s effects. Then, ＜Blitz＞ (If your opponent has 1 or more memory, this Digimon may attack). [All Turns] While a card with the [Royal Knight]&#160;trait is in this Digimon''s digivolution cards, this Digimon gains ＜Piercing＞ (When this Digimon attacks and deletes an opponent''s Digimon and survives the battle, it performs any security checks it normally would) and ＜Blocker＞ (At blocker timing, by suspending this Digimon, it becomes the attack target), and gains ＜Security Attack +1＞ (This Digimon checks 1 additional security card) for each card with the [Royal Knight]&#160;trait in this Digimon''s digivolution cards.', NULL, 'BT-10: Booster Xros Encounter', 'https://images.digimoncard.io/images/cards/BT10-112.jpg', 'BT10', '112', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Quartzmon', 'Digimon', 'Green', 'Mega', 'Unidentified', 'Unknown', 7, 16, 6, 'Alternative Art', NULL, 15000, '[When Digivolving] Suspend all other Digimon and all Tamers. Then, for every 2 suspended Digimon and Tamer in play, gain 1 memory. [All Turns] All other Digimon and Tamers don''t unsuspend. [When Attacking] Suspend 1 of your opponent''s Digimon or Tamers. Then, trash the top card of your opponent’s security stack for every 5 suspended Digimon and Tamers.', NULL, 'BT-12: Booster Across Time', 'https://images.digimoncard.io/images/cards/BT12-057.jpg', 'BT12', '057', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('DarknessBagramon', 'Digimon', 'Purple', 'Mega', 'Composite', 'Virus', 7, 16, 6, 'Alternative Art', NULL, 15000, '[On Play] [When Digivolving] Delete 1 of your opponent''s Digimon. Then, you may place up to 5 Digimon cards with the [Bagra Army]&#160;trait from your trash as digivolution cards of this Digimon. [Opponent''s Turn] When an opponent''s Digimon digivolves or attacks, by trashing 5 cards in this Digimon''s digivolution cards, return all Tamers to their owners'' hands.', NULL, 'BT-12: Booster Across Time', 'https://images.digimoncard.io/images/cards/BT12-111.jpg', 'BT12', '111', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('ShineGreymon: Burst Mode', 'Digimon', 'Red', 'Mega', 'Light Dragon', 'Vaccine', 7, 15, 5, 'Alternative Art', NULL, 15000, '[When Digivolving] You may play 1 [Marcus Damon] from your hand without paying its cost. For the turn, treat the Tamer played with this effect as a 12000 DP Digimon with ＜Rush＞ (This Digimon can attack the turn it comes into play), and it cannot digivolve. [Your Turn] [Once Per Turn] When one of your Tamers becomes suspended, Trash 1 card from the top of your opponent’s security stack.', NULL, 'BT-13: Booster Versus Royal Knights', 'https://images.digimoncard.io/images/cards/BT13-020.jpg', 'BT13', '020', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('MirageGaogamon: Burst Mode', 'Digimon', 'Blue', 'Mega', 'Beast Knight', 'Data', 7, 15, 5, 'Alternative Art', NULL, 15000, '[When Digivolving] Return 1 of your opponent''s Digimon to its owner''s hand. Then, gain 1 memory for every 4 cards in your opponent''s hand. [When Attacking] If your opponent has 9 or more cards in their hand, by returning random cards from their hand to the bottom of their owner''s deck without looking at them until they have only 8 cards left, unsuspend this Digimon.', NULL, 'BT-13: Booster Versus Royal Knights', 'https://images.digimoncard.io/images/cards/BT13-033.jpg', 'BT13', '033', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Rosemon:  Burst Mode', 'Digimon', 'Green', 'Mega', 'Fairy', 'Data', 7, 15, 5, 'Alternative Art', NULL, 15000, '[When Digivolving] Suspend 1 of your opponent''s Digimon and 1 of their Tamers. Until the end of your opponent''s turn, all of their Digimon and Tamers don''t unsuspend. [When Attacking] Trash the top card of your opponent’s security stack for every 2 of your opponent''s suspended Digimon or Tamers.', NULL, 'BT-13: Booster Versus Royal Knights', 'https://images.digimoncard.io/images/cards/BT13-060.jpg', 'BT13', '060', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Ravemon: Burst Mode', 'Digimon', 'Purple', 'Mega', 'Cyborg', 'Vaccine', 7, 15, 5, 'Alternative Art', NULL, 15000, '[When Digivolving] Search your opponent''s hand, choose 1 card, and trash it. Then, if your opponent has 7 or less cards in their hand, your opponent adds the top card of their security stack to their hand. [When Attacking] By returning 1 Digimon card from your opponent''s trash to the bottom of your their deck, delete all of your opponent''s Digimon with the samename as that card.', NULL, 'BT-13: Booster Versus Royal Knights', 'https://images.digimoncard.io/images/cards/BT13-092.jpg', 'BT13', '092', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Apocalymon', 'Digimon', 'White', 'Mega', 'Unidentified', 'Unknown', 7, 15, 6, 'Alternative Art', NULL, 15000, 'When this card would be played, by placing up to 3 [Dark Masters]&#160;trait cards with different names from your battle area or trash under it, reduce the play cost by 4 for each one. [End of Your Turn] [Once Per Turn] By placing 1 level 6 or lower card from your trash as this Digimon''s bottom digivolution card, activate 1 [On Play] effect on that card as an effect of this Digimon. Then, trash the top 2 cards of your opponent''s deck for each of this Digimon''s level 6 digivolution cards.', NULL, 'BT-15: Booster Exceed Apocalypse', 'https://images.digimoncard.io/images/cards/BT15-102.jpg', 'BT15', '102', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Chaosmon', 'Digimon', 'Yellow', 'Mega', 'Unique', 'Vaccine', 7, 15, 5, 'Alternative Art', NULL, 15000, '＜Barrier＞ (When this Digimon would be deleted in battle, by trashing the top card of your security stack, prevent that deletion) ＜Blocker＞ (At blocker timing, by suspending this Digimon, it becomes the attack target) ＜Partition (yellow Lv.6 + black Lv.6)＞ (When this Digimon with each of the specified digivolution cards would leave the battle area other than by your own effects or by battle, you may play 1 each of the specified cards without paying the costs) [When Digivolving] ＜De-Digivolve 3＞ 1 of your opponent''s Digimon (Trash up to 3 cards from the top. You can''t trash past level 3 cards). Then, 1 of their Digimon gets -8000 DP for the turn. [End of Opponent''s Turn] Trash the top card of both players'' security stacks. [Rule] Trait: Has the [Boss]/[D-Brigade]&#160;trait.', NULL, 'BT-16: Booster Beginning Observer', 'https://images.digimoncard.io/images/cards/BT16-036.jpg', 'BT16', '036', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Gallantmon: Crimson Mode', 'Digimon', 'Red', 'Mega', 'Holy Warrior', 'Virus', 7, 8, 5, 'Alternative Art', NULL, 15000, '[Hand] [Counter] ＜Blast Digivolve＞ (Your Digimon may digivolve into this card without paying the cost). [On Play] [When Digivolving] Choose any number of your opponent''s Digimon whose total DP adds up to 15000 or less and delete them. [When Attacking] [Once Per Turn] For every 10 cards in both player''s trash, trash 1 card from the top of your opponent''s security stack.', 'Ace Overflow ＜-5＞ (As this card moves from the battle area or under a card to another area, lose 5 memory.)', 'BT-17: Booster Secret Crisis', 'https://images.digimoncard.io/images/cards/BT17-018.jpg', 'BT17', '018', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('ShineGreymon: Burst Mode', 'Digimon', 'Yellow', 'Mega', 'Light Dragon', 'Vaccine', 7, 8, 5, 'Alternative Art', NULL, 15000, '[Hand] [Counter] ＜Blast Digivolve＞ (Your Digimon may digivolve into this card without paying the cost). [On Play] [When Digivolving] You may play 1 Tamer card from your hand without paying the cost. Then, for the turn, 1 of your opponent''s Digimon gets -5000 DP for each of your Tamers. [When Attacking] By suspending up to 2 of your yellow Tamers, for every Tamer this effect suspended, this Digimon gains ＜Security Attack +1＞ (This Digimon checks 1 additional security card) for the turn.', 'Ace Overflow ＜-5＞ (As this card moves from the battle area or under a card to another area, lose 5 memory.)', 'BT-17: Booster Secret Crisis', 'https://images.digimoncard.io/images/cards/BT17-041.jpg', 'BT17', '041', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Armageddemon', 'Digimon', 'Black', 'Mega', 'Unidentified', 'Unknown', 7, 16, 7, 'Alternative Art', NULL, 15000, 'When you would play this card from the hand, by placing up to 13 cards with the [Unidentified]&#160;trait or [Diaboromon]&#160;in its text from your trash at the bottom of your deck, reduce the cost by 1 for each card. ＜Rush＞ (This Digimon can attack the turn it comes into play). ＜Blocker＞ (At blocker timing, by suspending this Digimon, it becomes the attack target). ＜Reboot＞ (Unsuspend this Digimon during your opponent''s unsuspend phase). [On Play] [When Digivolving] Delete any of your opponent''s Digimon with a total play cost up to 15. [Your Turn] This Digimon can attack your opponent''s unsuspended Digimon.', NULL, 'BT-17: Booster Secret Crisis', 'https://images.digimoncard.io/images/cards/BT17-060.jpg', 'BT17', '060', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Omnimon', 'Digimon', 'White', 'Mega', 'Holy Warrior', 'Vaccine', 7, 9, 5, 'Alternative Art', NULL, 15000, '[Hand] [Counter] ＜Blast DNA Digivolve ([WarGreymon] + [MetalGarurumon])＞ (1 of your specified Digimon and 1 specified card in hand may DNA digivolve into this card.) ＜Raid＞ (When this Digimon attacks, you may switch the target of attack to 1 of your opponent''s unsuspended Digimon with the highest DP). ＜Blocker＞ (At blocker timing, by suspending this Digimon, it becomes the attack target). [On Play] [When Digivolving] If DNA Digivolving, choose 1 opponent''s Digimon and return the chosen Digimon and all of your opponent''s Digimon with the same level to the bottom of the deck. Then, delete 1 of your opponent''s Digimon.', 'Ace Overflow ＜-5＞ (As this card moves from the battle area or under a card to another area, lose 5 memory.)', 'BT-17: Booster Secret Crisis', 'https://images.digimoncard.io/images/cards/BT17-078.jpg', 'BT17', '078', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Omnimon Alter-S', 'Digimon', 'White', 'Champion', 'Holy Warrior', 'Virus', 7, 15, 6, 'Alternative Art', 'sasasi', 15000, '[When Digivolving] ＜De-Digivolve 1＞ all of your opponent''s Digimon (Trash the top card. You can''t trash past level 3 cards). Then, delete all of your opponent''s Digimon with 5000 DP or less. [When Attacking] You may make this Digimon unblockable by your opponent for the turn by returning one of its level 6 digivolution cards to your hand.', NULL, 'BT-03: Booster Union Impact', 'https://images.digimoncard.io/images/cards/BT3-112.jpg', 'BT3', '112', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Armageddemon', 'Digimon', 'White', 'Mega', 'Unidentified', 'Unknown', 7, 15, 3, 'Alternative Art', 'sasasi', 15000, 'When you would play this card from your hand, you may delete 1 of your [Diaboromon] to reduce this card''s play cost by 12. ＜Rush＞ (This Digimon can attack the turn it comes into play). [All Turns] The [When Digivolving] effects on level 7 Digimon don''t activate.', NULL, 'BT-05: Booster Battle Of Omni', 'https://images.digimoncard.io/images/cards/BT5-085.jpg', 'BT5', '085', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Omnimon Zwart', 'Digimon', 'White', 'Mega', 'Holy Warrior', 'Vaccine', 7, 15, 6, 'Alternative Art', 'Naochika Morishita', 15000, '[When Digivolving] Trash the top 3 cards of your deck. Then, you may play up to 2 play cost 8 or less black or purple Digimon cards from your trash without paying the costs. [When Attacking] By returning 1 level 6 Digimon card in this Digimon''s digivolution cards to the hand, delete 1 of your opponent''s unsuspended Digimon with a play cost of 12 or less.', NULL, 'BT-05: Booster Battle Of Omni', 'https://images.digimoncard.io/images/cards/BT5-087.jpg', 'BT5', '087', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Omnimon (X Antibody)', 'Digimon', 'White', 'Mega', 'Holy Warrior', 'Vaccine', 7, 15, 6, 'Alternative Art', 'As''Maria', 15000, 'Your Digimon with [Omnimon]&#160;in its name can digivolve into this card in your hand for a cost of 3, ignoring digivolution requirements. [When Attacking] Delete 1 of your opponent''s Digimon with DP less than or equal to this Digimon''s DP. [Opponent''s Turn] When an opponent''s Digimon attacks, by trashing 2 of this Digimon''s digivolution cards, end the attack.', NULL, 'BT-05: Booster Battle Of Omni', 'https://images.digimoncard.io/images/cards/BT5-111.jpg', 'BT5', '111', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Susanoomon', 'Digimon', 'White', 'Ultimate', 'Shaman', 'Vaccine', 7, 15, 7, 'Alternative Art', 'Tonamikanji', 15000, 'By placing 10 Tamer cards and/or cards with the [Hybrid]&#160;trait from your hand and/or trash at the bottom of your deck in any order, you may digivolve this card from your hand onto one of your Tamers as if the Tamer is a level 6 Digimon. ＜Security Attack +2＞ (This Digimon checks 2 additional security cards) [When Digivolving] Delete 1 of your opponent''s Digimon.', NULL, 'BT-07: Booster Next Adventure', 'https://images.digimoncard.io/images/cards/BT7-112.jpg', 'BT7', '112', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Ordinemon', 'Digimon', 'Purple', 'Champion', 'Fallen Angel', 'Virus', 7, 15, 6, 'Alternative Art', 'Tonamikanji', 15000, '[When Digivolving] When DNA digivolving, delete 1 of your opponent''s level 6 or higher Digimon and all of their level 5 or lower Digimon. For each Digimon deleted by this effect, ＜Recovery +1 (Deck)＞ (Place the top card of your deck on top of your security stack). [On Deletion] You may trash the top card of your security stack to play this card without paying its memory cost.', NULL, 'BT-09: Booster X Record', 'https://images.digimoncard.io/images/cards/BT9-082.jpg', 'BT9', '082', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Omnimon: Merciful Mode', 'Digimon', 'White', 'Champion', 'Holy Warrior', 'Vaccine', 7, 15, 6, 'Alternative Art', 'Tonamikanji', 15000, '[When Digivolving] For each card with the [Mega]&#160;trait in this Digimon''s digivolution cards, delete 1 of your opponent''s Digimon. Then, place 10 cards in your opponent''s trash at the bottom of their deck in any order. [Start of Your Turn] Trash the top card of this Digimon. If you do, trash the top card of your opponent''s security stack.', NULL, 'BT-09: Booster X Record', 'https://images.digimoncard.io/images/cards/BT9-083.jpg', 'BT9', '083', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('DeathXmon', 'Digimon', 'Purple', 'Mega', 'Unanalyzable', 'Virus', 7, 20, 6, 'Alternative Art', NULL, 15000, 'When you would play this card, reduce its memory cost by 3 for each Digimon and Tamer your opponent has in play. [On Play] [When Digivolving] ＜De-Digivolve 1＞ all of your opponent''s Digimon (Trash the top card. You can''t trash past level 3 cards). Then, delete all of your opponent''s level 4 or lower Digimon. [End of Opponent''s Turn] [Once Per Turn] Delete all of your opponent''s Digimon with the lowest play cost.', NULL, 'BT-09: Booster X Record', 'https://images.digimoncard.io/images/cards/BT9-112.jpg', 'BT9', '112', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Mother D-Reaper', 'Digi-Egg', 'White', NULL, 'Ability Synthesis Agent', NULL, 0, NULL, NULL, 'Rare', NULL, 15000, '[All Turns] This Digimon can''t attack and isn''t affected by your opponent''s effects. [Main] [Once Per Turn] If you don''t have another [Mother D-Reaper] in play, place 1 of your [ADR-02 Searcher]s from in play or your hand under this Digimon as its bottom digivolution card. [Your Turn] [Once Per Turn] When you would play a card with the [D-Reaper]&#160;trait from your hand, you may reduce its play cost by 1 for each of this Digimon''s digivolution cards.', NULL, 'EX-02: Theme Booster Digital Hazard', 'https://images.digimoncard.io/images/cards/EX2-007.jpg', 'EX2', '007', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Reaper', 'Digimon', 'White', NULL, 'Ability Synthesis Agent', NULL, 0, 20, NULL, 'Rare', NULL, 15000, 'When you would play this Digimon, you may trash 7 or more digivolution cards from the bottom of 1 of your [Mother D-Reaper]''s to set this Digimon''s play cost to 0. ＜Rush＞ (This Digimon can attack the turn it comes into play) [When Attacking] You may place 2 [ADR-02 Searcher]s from your trash under this Digimon in any order as its bottom digivolution cards to unsuspend this Digimon.', NULL, 'EX-02: Theme Booster Digital Hazard', 'https://images.digimoncard.io/images/cards/EX2-055.jpg', 'EX2', '055', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Gallantmon Crimson Mode', 'Digimon', 'Red', 'Ultimate', 'Holy Warrior', 'Virus', 7, 15, 6, 'Alternative Art', NULL, 15000, '[When Digivolving] Delete all of your opponent''s Digimon with the highest DP. [When Attacking] Trash the top card of your opponent''s security stack. Add 1 to the number of cards trashed by this effect for every 10 cards in your opponent''s trash.', NULL, 'EX-02: Theme Booster Digital Hazard', 'https://images.digimoncard.io/images/cards/EX2-073.jpg', 'EX2', '073', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Beelzemon Blast Mode', 'Digimon', 'Purple', 'Mega', 'Demon Lord', 'Virus', 7, 15, 6, 'Alternative Art', NULL, 15000, 'When this card is trashed from your deck, delete 1 of your opponent''s level 4 or lower Digimon. [When Digivolving] Delete all of your opponent''s Digimon with the highest level. [Your Turn] For every 10 cards in your trash, this Digimon gains ＜Security Attack +1＞ (This Digimon checks 1 additional security card).', NULL, 'EX-02: Theme Booster Digital Hazard', 'https://images.digimoncard.io/images/cards/EX2-074.jpg', 'EX2', '074', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Examon', 'Digimon', 'Green', 'Mega', 'Holy Warrior', 'Data', 7, 15, 5, 'Alternative Art', NULL, 15000, '[When Digivolving] You may place 1 green or blue Digimon card with [Dramon]&#160;in its name from your hand under this Digimon as its bottom digivolution card. When DNA digivolving, you may play 1 green or blue Digimon card with [Dramon]&#160;in its name and 12000 DP or less from your hand without paying the cost. [All Turns] [Once Per Turn] When this Digimon becomes suspended, unsuspend it, and suspend 1 of your opponent''s Digimon.', NULL, 'EX-03: Theme Booster Draconic Roar', 'https://images.digimoncard.io/images/cards/EX3-074.jpg', 'EX3', '074', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Omnimon Alter-S', 'Digimon', 'White', 'Mega', 'Holy Warrior', 'Virus', 7, 15, 6, 'Alternative Art', NULL, 15000, '[When Digivolving] Delete 1 of your opponent''s Digimon with 8000 DP or less, then return 1 of your opponent''s level 6 or higher Digimon to the bottom of its owner''s deck. [All Turns] When this Digimon would leave the battle area for a reason other than one of your effects, play 1 [BlitzGreymon] and 1 [CresGarurumon] from this Digimon''s digivolution cards without paying their cost. Then, place this Digimon on bottom of your security stack face down.', NULL, 'EX-04: Theme Booster Alternative Being', 'https://images.digimoncard.io/images/cards/EX4-060.jpg', 'EX4', '060', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Omnimon Alter-B', 'Digimon', 'Black', 'Mega', 'Holy Warrior', 'Virus', 7, 15, 5, 'Alternative Art', NULL, 15000, '[When Digivolving] ＜De-Digivolve 3＞ 1 of your opponent''s Digimon (Trash up to 3 cards from the top. You can''t trash past level 3 cards). Then, choose any number of your opponent''s Digimon so that their play cost total is up to 6 and delete them. [When Attacking] By trashing up to 3 level 6 or higher cards in this Digimon''s digivolution cards, delete 1 of your opponent''s Digimon or Tamers with the lowest play cost for each card trashed. If you trashed 3 cards, trash the top 2 cards of your opponent''s security stack.', NULL, 'EX-04: Theme Booster Alternative Being', 'https://images.digimoncard.io/images/cards/EX4-073.jpg', 'EX4', '073', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('ShineGreymon: Ruin Mode', 'Digimon', 'Purple', 'Mega', 'Light Dragon', 'Vaccine', 7, 14, 5, 'Alternative Art', NULL, 15000, '[When Digivolving] [On Deletion] Until the end of your opponent''s next turn, all of your opponent''s Digimon get -5000DP. [End of Attack] Delete this Digimon and 1 of your opponent''s Digimon, and ＜Recovery +1 (Deck)＞ (Place the top card of your deck on top of your security stack). Then, if you have a Tamer in play, hatch 1 Digi-Egg card to an empty space in your breeding area.', NULL, 'EX-04: Theme Booster Alternative Being', 'https://images.digimoncard.io/images/cards/EX4-074.jpg', 'EX4', '074', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('GraceNovamon', 'Digimon', 'Red', 'Mega', 'Galaxy', 'Vaccine', 7, 15, 6, 'Alternative Art', NULL, 15000, '＜Security Attack +1＞ (This Digimon checks 1 additional security card) ＜Blocker＞ (At blocker timing, by suspending this Digimon, it becomes the attack target) [When Digivolving] [When Attacking] If DNA digivolving, trash any 8 digivolution cards from your opponent''s Digimon. Then, delete 1 of their Digimon with as many or fewer digivolution cards as this Digimon. [All Turns] When this Digimon would leave the battle area by an opponent''s effect, by trashing 2 same-level cards in this Digimon''s digivolution cards, prevent it from leaving.', NULL, 'EX-05: Theme Booster Animal Colosseum', 'https://images.digimoncard.io/images/cards/EX5-073.jpg', 'EX5', '073', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Fanglongmon', 'Digimon', 'Yellow', 'Mega', 'God Beast', 'NO DATA', 7, 15, 6, 'Alternative Art', NULL, 15000, '[On Play] [When Attacking] By returning up to 4 cards with the [Deva]/[Four Sovereigns]&#160;trait from your trash to the bottom of the deck, for each one, all your opponent''s Digimon get -4000 DP for the turn. [When Attacking] For each of your Digimon with the [Four Sovereigns]&#160;trait, trash the top card of your opponent''s security stack. [All Turns] This Digimon isn''t affected by the effects of your opponent''s Digimon.', NULL, 'EX-05: Theme Booster Animal Colosseum', 'https://images.digimoncard.io/images/cards/EX5-074.jpg', 'EX5', '074', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('RagnaLoardmon', 'Digimon', 'Red', 'Mega', 'Unique', 'Virus', 7, 9, 5, 'Alternative Art', NULL, 15000, '[Hand] [Counter] ＜Blast DNA Digivolve ([Durandamon] + [BryweLudramon])＞ (1 of your specified Digimon and 1 specified card in hand may DNA digivolve into this card.) ＜Raid＞ (When this Digimon attacks, you may switch the target of attack to 1 of your opponent''s unsuspended Digimon with the highest DP). ＜Reboot＞ (Unsuspend this Digimon during your opponent''s unsuspend phase). [On Play] [When Digivolving] Trash the top card of your opponent''s security stack and Digimon isn''t affected by your opponent''s effects until the end of their turn. Then, if DNA digivolving, ＜De-Digivolve 1＞ all of your opponent''s Digimon (Trash the top card. You can''t trash past level 3 cards) and delete 1 of their Digimon.', 'Ace Overflow ＜-5＞ (As this card moves from the battle area or under a card to another area, lose 5 memory.)', 'EX-06: Theme Booster Infernal Ascension', 'https://images.digimoncard.io/images/cards/EX6-011.jpg', 'EX6', '011', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Shakamon', 'Digimon', 'Yellow', 'Mega', 'Tathāgata', 'Vaccine', 7, 15, 4, 'Alternative Art', NULL, 15000, '[On Play] [When Digivolving] All Digimon gain ＜Security Attack -1＞ (This Digimon checks 1 fewer security card) until the end of your opponent''s turn. [All Turns] When this Digimon would be deleted or returned to the hand or deck, you may play 1 [Sanzomon] and 1 [Gokuumon]/[Sagomon]/[Cho-Hakkaimon] from its digivolution cards without paying the cost. [Your Turn] Change ＜Security Attack -＞ (This Digimon checks fewer security cards) to ＜Security Attack +＞ (This Digimon checks additional security cards) on all of your Digimon. [End of Opponent''s Turn] [Once Per Turn] You may place 1 Digimon with ＜Security Attack＞ on top of its owner''s security stack.', NULL, 'EX-06: Theme Booster Infernal Ascension', 'https://images.digimoncard.io/images/cards/EX6-031.jpg', 'EX6', '031', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Tlalocmon', 'Digimon', 'Green', 'Mega', 'Mineral', 'Data', 7, 15, 6, 'Alternative Art', NULL, 15000, '[When Digivolving] You may play 1 Digimon card with the [NSp]&#160;trait and a play cost of 7 or less from your hand without paying the cost. If DNA digivolving, you may play 2 Digimon cards with different colors and the [NSp]&#160;trait and a play cost of 7 or less from your hand without paying the cost instead. [When Digivolving] [When Attacking] [Once Per Turn] For each of your Digimon, 1 of your opponent''s Digimon gets -7000 DP for the turn.', NULL, 'EX-07: Extra Booster Digimon Liberator', 'https://images.digimoncard.io/images/cards/EX7-037.jpg', 'EX7', '037', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Proximamon', 'Digimon', 'Red', 'Mega', 'Unique', 'Virus', 7, 15, 6, 'Alternative Art', NULL, 15000, '[All Turns] [Once Per Turn] When another Digimon is deleted, you may play 1 level 4 or lower Digimon card with [Gammamon]&#160;in its name from your trash without paying the cost. [End of Your Turn] [Once Per Turn] By placing 1 Digimon card with [Gammamon]&#160;in its text from your hand or trash as this Digimon''s bottom digivolution card, delete 1 of your opponent''s Digimon with DP less than or equal to this Digimon''s DP.', NULL, 'RB-01: Reboot Booster Rising Wind', 'https://images.digimoncard.io/images/cards/RB1-036.jpg', 'RB1', '036', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('RagnaLoardmon', 'Digimon', 'Red', 'Mega', 'Unique', 'Virus', 7, 15, 6, 'Super Rare', 'sasasi', 15000, '[When Digivolving] ＜Blitz＞ (If your opponent has 1 or more memory, this Digimon may attack). When DNA digivolving, for every 4 cards in this Digimon''s digivolution cards, delete 1 of your opponent''s Digimon with a play cost of 20 or less and trash the top card of your opponent''s security stack. [All Turns] [Once Per Turn] When a card is removed from a player''s security stack, unsuspend this Digimon.', NULL, 'ST-13: Starter Deck RagnaLoardmon', 'https://images.digimoncard.io/images/cards/ST13-06.jpg', 'ST13', '06', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Omnimon (X Antibody)', 'Digimon', 'White', 'Mega', 'Holy Warrior', 'Vaccine', 7, 16, 7, 'Alternative Art', NULL, 16000, 'When a Digimon with [X Antibody] in its digivolution cards would digivolve into this card, reduce the digivolution cost by 2. [When Digivolving] Return all of your opponent''s Digimon with the highest level to the bottom of their owners'' decks in any order. [When Digivolving] [When Attacking] [Once Per Turn] By placing 1 [X Antibody] or level 6 card from this Digimon''s digivolution cards at the bottom of its owner''s deck, reveal all of your opponent''s security cards, and trash 1 of them. Place the rest in your opponent''s security stack face down. Then, your opponent shuffles their security stack.', NULL, 'BT-10: Booster Xros Encounter', 'https://images.digimoncard.io/images/cards/BT10-086.jpg', 'BT10', '086', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Imperialdramon: Paladin Mode', 'Digimon', 'White', 'Mega', 'Ancient Holy Warrior', 'Vaccine', 7, 9, 6, 'Alternative Art', NULL, 16000, '[Hand] [Counter] ＜Blast Digivolve＞ (Your Digimon may digivolve into this card without paying the cost). [On Play] [When Digivolving] Trash all digivolution cards of all of your opponent''s Digimon. Then, return all cards from your or your opponent''s trash to the bottom of the deck. If this effect returned a white level 7 card, gain 3 memory. [When Attacking] By returning 1 of your opponent''s Digimon with no digivolution cards to the bottom of the deck, unsuspend this Digimon. [Rule] Trait: Has the [Free] attribute.', 'Ace Overflow ＜-5＞ (As this card moves from the battle area or under a card to another area, lose 5 memory.)', 'BT-17: Booster Secret Crisis', 'https://images.digimoncard.io/images/cards/BT17-077.jpg', 'BT17', '077', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Fenriloogamon: Takemikazuchi', 'Digimon', 'Purple', 'Mega', 'Dark Animal', 'Virus', 7, 16, 6, 'Alternative Art', NULL, 16000, '[Trash] [Your Turn] When one of your level 6 Digimon with [Pulsemon]&#160;in its text is played, 2 of your Digimon may DNA digivolve into this card. [When Digivolving] 1 of your opponent''s Digimon gets -16000 DP for the turn. If DNA digivolving, you may set your the opponent''s memory to 3. Then, if this Digimon has a Tamer in its digivolution cards, gain 1 memory and ＜Recovery +1 (Deck)＞ (Place the top card of your deck on top of your security stack). [When Attacking] By trashing the top card of your security stack, trash the top card of your opponent''s security stack.', NULL, 'BT-17: Booster Secret Crisis', 'https://images.digimoncard.io/images/cards/BT17-101.jpg', 'BT17', '101', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Imperialdramon Paladin Mode', 'Digimon', 'White', 'Ultimate', 'Ancient Holy Warrior', 'Vaccine', 7, 15, 7, 'Alternative Art', 'Tonamikanji', 16000, 'When one of your Digimon would digivolve into this card in your hand, you may return 1 white level 7 Digimon card from your trash to the bottom of your deck to reduce the digivolution cost by 4. [When Digivolving] [When Attacking] You may return 1 2-color card from this Digimon''s digivolution cards to the bottom of its owner''s deck to trash all of the digivolution cards of 1 of your opponent''s Digimon. Then, return all of your opponent''s Digimon with no digivolution cards to the bottom of their owners'' decks in any order.', NULL, 'BT-08: Booster New Awakening', 'https://images.digimoncard.io/images/cards/BT8-112.jpg', 'BT8', '112', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Alphamon: Ouryuken', 'Digimon', 'Black', 'Mega', 'NO DATA', 'Vaccine', 7, 15, 7, 'Alternative Art', 'Tonamikanji', 16000, '[When Digivolving] Delete all of your opponent''s Digimon with the highest play cost. [End of Your Turn] [Once Per Turn] You may return up to 7 non-Digi-Egg cards with the [X Antibody]&#160;trait from this Digimon''s digivolution cards to the bottom of your deck in any order, if you do, gain 1 memory for each card returned.', NULL, 'BT-09: Booster X Record', 'https://images.digimoncard.io/images/cards/BT9-111.jpg', 'BT9', '111', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('UltimateChaosmon', 'Digimon', 'White', 'Ultimate', 'Unique', 'Unknown', 7, 16, 7, 'Alternative Art', NULL, 16000, '＜Partition (Yellow/Black Lv.6 + Green/Purple Lv.6)＞ (When this Digimon with each of the specified digivolution cards would leave the battle area other than by your own effects or by battle, you may play 1 each of the specified cards without paying the costs). [When Digivolving] If DNA digivolving, you may place up to 2 level 6 cards from your trash as this Digimon''s bottom digivolution cards. Then, for each level 6 card in this Digimon''s digivolution cards, return 1 of your opponent''s Digimon to the bottom of the deck. [Your Turn] While this Digimon has 4 or more level 6 cards in its digivolution cards, it gains ＜Security Attack +3＞ (This Digimon checks 3 additional security cards) and ＜Piercing＞ (When this Digimon attacks and deletes an opponent''s Digimon and survives the battle, it performs any security checks it normally would).', NULL, 'EX-06: Theme Booster Infernal Ascension', 'https://images.digimoncard.io/images/cards/EX6-062.jpg', 'EX6', '062', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Ogudomon', 'Digimon', 'Purple', 'Mega', 'Avatar', 'Virus', 7, 16, 7, 'Alternative Art', NULL, 16000, '[When Digivolving] [When Attacking] You may place up to 7 cards with different names and the [Seven Great Demon Lords]&#160;trait from your trash as this Digimon''s bottom digivolution cards. If you placed 4 or more cards with this effect, delete 1 of your opponent''s Digimon or Tamers. [When Attacking] By returning 7 cards with different names and the [Seven Great Demon Lords]&#160;trait from this Digimon''s digivolution cards to the bottom of the deck, delete 7 of your opponent''s Digimon or Tamers. Then, trash the top 7 cards of your opponent''s security stack. For each card deleted by this effect, reduce the cards trashed by 1.', NULL, 'EX-06: Theme Booster Infernal Ascension', 'https://images.digimoncard.io/images/cards/EX6-073.jpg', 'EX6', '073', 'DG');
INSERT INTO cartas (name, type, color, stage, digi_type, attribute, level, play_cost, evolution_cost, cardrarity, artist, dp, maineffect, soureeffect, set_name, image_url, id_coleccion, id_carta, id_juego) VALUES ('Shoutmon X7: Superior Mode', 'Digimon', 'White', 'Mega', 'Composite', 'Data', 7, 15, NULL, 'Alternative Art', NULL, 17000, 'When you would play this card, by placing 1 [Shoutmon] as a digivolution card under this Digimon, reduce the cost by 1 and you may place cards in your trash as digivolution cards for a DigiXros. [On Play] Return all of the digivolution cards of 1 of your opponent''s Digimon to the bottom of its owner''s deck, and return that Digimon to the bottom of its owner''s deck. [Your Turn] All of your opponent''s [Security] effects on Option cards don''t activate.', NULL, 'BT-12: Booster Across Time', 'https://images.digimoncard.io/images/cards/BT12-112.jpg', 'BT12', '112', 'DG');
